import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const originalPush = VueRouter.prototype.push
VueRouter.prototype.push = function push(location) {
  return originalPush.call(this, location).catch(err => err)
}

const routes = [
  {
    path: '/',
    redirect: '/front/home',  // 默认重定向到新闻首页
  },
  {
    path: '/manager',
    name: 'Manager',
    component: () => import('../views/Manager.vue'),
    children: [
      { path: '403', name: 'NoAuth', meta: { name: '无权限' }, component: () => import('../views/manager/403') },
      { path: 'home', name: 'Home', meta: { name: '系统首页' }, component: () => import('../views/manager/Home') },
      { path: 'admin', name: 'Admin', meta: { name: '管理员信息' }, component: () => import('../views/manager/Admin') },
      { path: 'adminPerson', name: 'AdminPerson', meta: { name: '个人信息' }, component: () => import('../views/manager/AdminPerson') },
      { path: 'password', name: 'Password', meta: { name: '修改密码' }, component: () => import('../views/manager/Password') },
      { path: 'notice', name: 'Notice', meta: { name: '公告信息' }, component: () => import('../views/manager/Notice') },
      { path: 'provinces', name: 'Provinces', meta: { name: '省份信息' }, component: () => import('../views/manager/Provinces') },
      { path: 'news', name: 'News', meta: { name: '新闻公告' }, component: () => import('../views/manager/News') },
    ]
  },
  {
    path: '/front',
    name: 'Front',
    component: () => import('../views/Front.vue'),
    children: [
      { path: 'home', name: 'Home', meta: { name: '系统首页' }, component: () => import('../views/front/Home') },
      { path: 'person', name: 'Person', meta: { name: '个人信息' }, component: () => import('../views/front/Person') },
      { path: 'newsDetail', name: 'FNewsDetail', component: () => import('../views/front/NewsDetail') },
      { path: 'provinceNews', name: 'ProvinceNews', component: () => import('../views/front/ProvinceNews') },
    ]
  },
  { path: '/login', name: 'Login', meta: { name: '登录' }, component: () => import('../views/Login.vue') },
  { path: '/register', name: 'Register', meta: { name: '注册' }, component: () => import('../views/Register.vue') },
  { path: '*', name: 'NotFound', meta: { name: '无法访问' }, component: () => import('../views/404.vue') },
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

// 路由守卫
router.beforeEach((to, from, next) => {
  let user = JSON.parse(localStorage.getItem("xm-user") || '{}');
  console.log("当前路径: ", to.path);
  console.log("用户角色: ", user.role);

  if (to.path === '/') {
    if (!user.role) {
      next('/front/home');  // 未登录用户访问根路径时，跳转到新闻首页
    } else if (user.role === 'ADMIN') {
      next('/home');  // 已登录的管理员跳转到后台首页
    } else {
      next('/login');  // 任何其他情况，跳转到登录页
    }
  } else if (to.path === '/front/home' && !user.role) {
    next();  // 未登录用户允许访问新闻首页
  } else if (user.role === 'ADMIN' && to.path.startsWith('/manager')) {
    next();  // 已登录管理员允许访问管理页
  } else {
    next('/login');  // 其他情况一律跳转到登录页
  }
})

export default router
